CREATE TABLE `wd_xcx_main_shop_order` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `uniacid` int(11) NOT NULL COMMENT 'uniacid',
  `suid` int(10) NOT NULL COMMENT '用户ID',
  `source` int(2) NOT NULL COMMENT '下单小程序端',
  `order_id` varchar(32) NOT NULL COMMENT '订单号',
  `order_service_id` varchar(32) DEFAULT NULL COMMENT '售后订单号',
  `goods_total_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '商品总价',
  `pay_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '订单实付总价',
  `discount_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '订单总优惠金额',
  `freight_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '订单总配送费金额 ',
  `creat_time` int(11) NOT NULL DEFAULT '0' COMMENT '订单创建时间',
  `pay_time` int(11) NOT NULL DEFAULT '0' COMMENT '订单支付时间',
  `address_info` varchar(1000) NOT NULL DEFAULT '' COMMENT '收货地址信息',
  `self_taking_info` varchar(1000) NOT NULL DEFAULT '' COMMENT '自取信息 时间 联系电话 自取门店',
  `buyer_mobile` varchar(20) NOT NULL COMMENT '购买人联系方式',
  `score_sent` int(11) DEFAULT '0' COMMENT '订单赠送总积分',
  `score_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '订单积分抵扣的金额 ',
  `score_use` int(11) DEFAULT '0' COMMENT '订单总抵扣积分',
  `coupon_use` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '优惠券抵扣金额',
  `coupon_id` int(11) DEFAULT '0' COMMENT '使用优惠券ID, ID是领取记录里面的',
  `user_remark` varchar(400) DEFAULT NULL COMMENT '用户备注',
  `formlist_id` int(11) DEFAULT NULL COMMENT '使用表单ID',
  `formlist_val` text COMMENT '提交表单内容',
  `formcon_id` int(11) NOT NULL DEFAULT '0' COMMENT '提交表单内容ID',
  `status` int(2) NOT NULL DEFAULT '0' COMMENT '订单状态 -3 付款后取消  -2 未付款取消  -1 未支付过期 0 未支付  1 待发货 2 待核销(全部发货待收货) 3 全部核销收货  4 付款后总订单取消申请 5 完成',
  `buy_data` varchar(600) NOT NULL DEFAULT '' COMMENT '购买商品信息  商品id|规格值id|数量',
  `cancel_time` int(11) NOT NULL DEFAULT '0' COMMENT '订单取消时间',
  `complete_time` int(11) NOT NULL DEFAULT '0' COMMENT '订单完成时间',
  `delivery_type` tinyint(1) NOT NULL COMMENT '订单配送方式  1 快递  2 自取 ',
  `allow_all_refund` tinyint(1) NOT NULL DEFAULT '1' COMMENT '允许统一退款  默认1  允许   2   不允许 ',
  `express` varchar(50) DEFAULT NULL COMMENT '统一发货快递公司',
  `express_no` varchar(50) DEFAULT NULL COMMENT '统一发货快递单号',
  `deliver_time` int(11) NOT NULL DEFAULT '0' COMMENT '发货时间',
  `pay_type` tinyint(1) NOT NULL DEFAULT '1' COMMENT '支付类型 1 余额  2 线上 ',
  `pay_to` tinyint(1) DEFAULT '0' COMMENT '支付到   1 微信 2 支付宝  3 百度 4 QQ',
  `is_change_price` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否修改价格 默认0 没修改  1 修改',
  `change_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '订单修改后总价',
  `change_price_remark` varchar(400) DEFAULT NULL COMMENT '订单价格修改备注',
  `form_id` varchar(50) NOT NULL DEFAULT '' COMMENT '模板信息formid',
  `payment_info` varchar(400) DEFAULT NULL COMMENT '支付信息',
  `total_num` int(11) DEFAULT '0' COMMENT '订单商品总数量',
  `total_can_tui_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '订单总可退款金额',
  `check_time` int(11) NOT NULL DEFAULT '0' COMMENT '订单核销时间',
  `check_info` varchar(200) DEFAULT NULL COMMENT '订单核销信息',
  `assess` tinyint(1) NOT NULL DEFAULT '1' COMMENT '订单是否评价  默认1  未评价  2  初评  3 追评完成',
  `is_fanxian` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否完成返现 默认 0  未完成  1 完成',
  `is_delete` tinyint(1) NOT NULL DEFAULT '0' COMMENT '删除  默认 0  没有删除  1  已删除',
  `from_gwc` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否从购物车下单 0  不是(直接购买)  1 是',
  `has_service` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否有售后 默认 0  没有  1   有',
  PRIMARY KEY (`id`),
  KEY `uniacid` (`uniacid`) USING BTREE,
  KEY `order_id` (`order_id`) USING BTREE,
  KEY `suid` (`suid`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=45 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;
