CREATE TABLE `wd_xcx_main_shop_order_item` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `uniacid` int(11) NOT NULL COMMENT 'uniacid',
  `suid` int(10) NOT NULL COMMENT '用户ID',
  `source` int(2) NOT NULL COMMENT '下单小程序端',
  `order_id` varchar(32) NOT NULL COMMENT '订单号',
  `order_item_id` varchar(32) NOT NULL COMMENT '子订单号',
  `order_service_id` varchar(50) DEFAULT NULL COMMENT '售后订单号',
  `num` int(10) NOT NULL DEFAULT '0' COMMENT '下单数量',
  `refund_num` int(10) NOT NULL DEFAULT '0' COMMENT '已退数量 默认 0',
  `pro_id` int(10) NOT NULL DEFAULT '0' COMMENT '下单商品ID',
  `pro_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '商品销售价',
  `pro_discounts` float(10,2) DEFAULT NULL COMMENT '会员折扣',
  `pro_discounts_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '商品折扣后价格',
  `pro_discounts_jian_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '商品折扣减去的价格',
  `pro_can_refound_price` decimal(10,2) DEFAULT NULL COMMENT '商品可退价格',
  `pro_attr` varchar(200) DEFAULT NULL COMMENT '商品规格',
  `pro_type_id` int(11) DEFAULT '0' COMMENT '商品规格值ID  -1为单规格 ',
  `pro_title` varchar(100) NOT NULL DEFAULT '' COMMENT '标题',
  `pro_thumb` varchar(200) NOT NULL DEFAULT '' COMMENT '缩略图',
  `pro_fx` varchar(1000) NOT NULL DEFAULT '' COMMENT '分销设置',
  `score_send_info` varchar(100) NOT NULL DEFAULT '' COMMENT '赠送积分',
  `creat_time` int(11) NOT NULL DEFAULT '0' COMMENT '订单创建时间',
  `pay_time` int(11) NOT NULL DEFAULT '0' COMMENT '订单支付时间',
  `delivery_type` tinyint(1) NOT NULL COMMENT '订单配送方式  1 快递  2 自取 ',
  `deliver_time` int(11) NOT NULL DEFAULT '0' COMMENT '订单发货时间',
  `express` varchar(50) DEFAULT NULL COMMENT '发货快递公司',
  `express_no` varchar(50) DEFAULT NULL COMMENT '发货快递单号',
  `received_time` int(11) NOT NULL DEFAULT '0' COMMENT '确认收货时间',
  `tui_time` int(11) NOT NULL DEFAULT '0' COMMENT '申请退货时间',
  `check_time` int(11) NOT NULL DEFAULT '0' COMMENT '订单核销时间',
  `check_info` varchar(100) DEFAULT NULL COMMENT '核销信息',
  `status` int(2) NOT NULL DEFAULT '0' COMMENT '订单状态 -5 订单退货 -4 付款后单独取消  -3 付款后统一取消  -2 未付款取消  -1 未支付过期 0 未支付  1  待发货  2 待核销(待收货) 3已核销 已收货 4 付款后总订单退款申请 5 付款后订单单独退款 6 订单退货 7 完成',
  `has_service` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否有售后 默认 0  没有  1   有',
  `complete_time` int(11) NOT NULL DEFAULT '0' COMMENT '订单完成时间',
  `cancel_time` int(11) NOT NULL DEFAULT '0' COMMENT '订单取消时间',
  `agree_tui_time` int(11) NOT NULL DEFAULT '0' COMMENT '同意订单退款时间，即为退款时间',
  `assess` tinyint(1) NOT NULL DEFAULT '1' COMMENT '是否已评论  1  未评论  2  已评论',
  `business_remark` varchar(500) DEFAULT NULL COMMENT '商家备注',
  `pro_type` tinyint(2) NOT NULL DEFAULT '1' COMMENT '商品类型 1 主商品 ',
  `order_item_log` varchar(1000) DEFAULT NULL COMMENT '订单日志记录',
  PRIMARY KEY (`id`),
  KEY `uniacid` (`uniacid`) USING BTREE,
  KEY `order_id` (`order_id`) USING BTREE,
  KEY `order_item_id` (`order_item_id`) USING BTREE,
  KEY `suid` (`suid`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=45 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;
